import React, { Component } from 'react'
import Newitem from './Newitem'
import Spinner from './Spinner';

export class News extends Component {
 
  
  constructor(){
    
    super();
    this.state = {
      article : [],
      loading : false,
      page : 1

    }
  }

  async componentDidMount(){
    this.props.setprogress(10);
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.Category}&apiKey=d490a3fd44e34e699f5c241e425ab41a&pageSize=${this.props.pagesize}`;
    let data = await fetch(url);
    let parsedata = await data.json();
    console.log(parsedata);
    this.setState ({
      article : parsedata.articles,
      totalarticle : parsedata.totalResults
    });
    

  }

  previousClick = async ()=>{
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.Category}&apiKey=d490a3fd44e34e699f5c241e425ab41a&page=${this.state.page-1}&pageSize=${this.props.pagesize}`;
    let data = await fetch(url);
    let parsedata = await data.json();
    console.log(parsedata);
    this.setState ({
      page: this.state.page-1,
      article : parsedata.articles
    });
  


  }
  nextClick = async ()=>{

    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.Category}&apiKey=d490a3fd44e34e699f5c241e425ab41a&page=${this.state.page+1}&pageSize=${this.props.pagesize}`;
    let data = await fetch(url);
    let parsedata = await data.json();
    console.log(parsedata);
    this.setState ({
      page: this.state.page+1,
      article : parsedata.articles
    });
  
  }


  render() {
   
    const containerStyle = {
      margin: '0px', // Set your desired margin value here
    };

    if (!this.state.article || this.state.article.length === 0) {
      return <div className='container  text-center'  >
        <Spinner></Spinner>
      </div>
    }
   
    return (

      <><div className='container ' >
        <h1 className='text-center'> Alert News - Headlines</h1>
            <br></br>


        <div className = "row">

        {this.state.article.map((element)=>{
          return   <div className='col-md-4' style={containerStyle}>
          <Newitem  title={element.title?element.title.slice(0,50):""} description={element.description?element.description.slice(0,85):""} imgUrl={element.urlToImage} 
          newsUrl ={element.url} author = {!element.author?"Unknown":element.author} date = {element.publishedAt} source = {element.source.name}></Newitem>
          <br></br>
 
          </div>
         
          
        })}
        
        
        </div>
        <div class = "container d-flex justify-content-between">
        <button type="button" disabled={this.state.page<=1} class="btn btn-success" onClick={this.previousClick}>&laquo; Pervious</button>
        <button type="button" disabled ={this.state.page +1> Math.ceil(this.state.totalarticle/18)}class="btn btn-success" onClick={this.nextClick}> Next &raquo;</button>
        </div>
        
       
        </div></>
    )
  }
}

export default News
