import React, { Component } from 'react'
import Newitem from './Newitem'

export class News extends Component {
 
  
  constructor(){
    super();
    this.state = {
      article : [],
      loading : false

    }
  }

  async componentDidMount(){
    let url = "https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=5f81f3a0c2b64a42b7a8c26a53437e60";
    let data = await fetch(url);
    let parsedata = await data.json();
    console.log(parsedata);
    this.setState ({article : parsedata.articles});
    

  }
  render() {
   
    const containerStyle = {
      margin: '20px', // Set your desired margin value here
    };
    return (

      <><div className='container ' >
            <h2>Alert News - Headlines</h2>
            <br></br>


        <div className = "row">

        {this.state.article.map((element)=>{
          return   <div className='col-md-4' style={containerStyle}>
          <Newitem  title={element.title?element.title.slice(0,60):""} description={element.description?element.description.slice(0,95):""} imgUrl={element.urlToImage} newsUrl ={element.url}></Newitem>
 
          </div>
         
          
        })}
        
        
        </div>
        
       
        </div></>
    )
  }
}

export default News
