import React, { Component } from 'react'

export class Newitem extends Component {
  render() {
    let {title,description,imgUrl,newsUrl} = this.props;
    return (
        <div className="card" style={{width:" 18rem" ,height :"490px"}}><span class="badge rounded-pill text-bg-warning">Warning</span>
          
        <img src={!imgUrl?"https://img.freepik.com/free-vector/realistic-news-studio-background_23-2149985600.jpg?w=2000":imgUrl} alt="..."></img>
        <div className="card-body">
          <h5 className="card-title">{title}...</h5>
          <p className="card-text">{description}...</p>
          <p class="card-text">By {this.props.author} on {this.props.date}</p>
          <a href={newsUrl} target =  "_blank" className="btn btn-sm btn-success">Read more</a>
        </div>
      </div>
    )
  }
}

export default Newitem
