import React, { Component } from 'react'
import Newitem from './Newitem'

export class News extends Component {
 
  constructor(){
    super();
    this.state = {
      article : [],
      loading : false

    }
  }

  async componentDidMount(){
    let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=d490a3fd44e34e699f5c241e425ab41a";
    let data = await fetch(url);
    let parsedata = await data.json();
    console.log(parsedata);
    this.setState = ({article : parsedata.article});
    

  }
  render() {

    return (

      <><div className='container my-3  '>
        <h2>NewsApp - Headlines</h2>
        <div className = "row">

        {this.state.article.map((element)=>{
          return   <div className='col-md-4' key = {element.url} >
          <Newitem target = "blank" title={element.title?element.title.slice(0,60):element=""} description={element.description?element.description.slice(0,95):""} imgUrl={element.urlToImage} newsUrl ={element.url}></Newitem>
 
          </div>
         
          
        })}
        
        
        </div>
        
       
        </div></>
    )
  }
}

export default News
